import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.Scanner; import java.util.StringTokenizer;import java.util.*;public class Main {    static class FastReader     {         BufferedReader br;         StringTokenizer st;          public FastReader()         {             br = new BufferedReader(new                     InputStreamReader(System.in));         }          String next()         {             while (st == null || !st.hasMoreElements())             {                 try                {                     st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e)                 {                     e.printStackTrace();                 }             }             return st.nextToken();         }          int nextInt()         {             return Integer.parseInt(next());         }          long nextLong()         {             return Long.parseLong(next());         }          double nextDouble()         {             return Double.parseDouble(next());         }          String nextLine()         {             String str = "";             try            {                 str = br.readLine();             }             catch (IOException e)             {                 e.printStackTrace();             }             return str;         }     }    public static void main(String[] args) {       FastReader sc=new FastReader();      int t=sc.nextInt();      while(t-->0){        int n=sc.nextInt();        int a1=0,b1=0,a2=0,b2=0;        for(int i=0;i<n;i++){           String s=sc.nextLine();              if(i==0){a1=s.charAt(1)-'0';}              if(i==1){b1=s.charAt(0)-'0';}              if(i==n-2){a2=s.charAt(n-1)-'0';}              if(i==n-1){b2=s.charAt(n-2)-'0';}        }        if(a1==0 && b1==0){            if(a2==0 && b2==0){System.out.println("2");                         System.out.println("1 2");                         System.out.println("2 1");            }            if(a2==1 && b2==1){System.out.println("0");}            if(a2==1 && b2==0){System.out.println("1");                   System.out.println((n)+" "+(n-1));            }            if(a2==0 && b2==1){System.out.println("1");                   System.out.println((n-1)+" "+(n));}        }        if(a1==1 && b1==0){            if(a2==0 && b2==0){System.out.println("1");                   System.out.println("2 1");}            if(a2==1 && b2==1){System.out.println("1");                   System.out.println("1 2");}            if(a2==1 && b2==0){System.out.println("2");                         System.out.println("1 2");                         System.out.println(n+" "+(n-1));}            if(a2==0 && b2==1){System.out.println("2");                         System.out.println("1 2");                         System.out.println((n-1)+" "+n);}        }        if(a1==0 && b1==1){            if(a2==0 && b2==0){System.out.println("1");                   System.out.println("1 2");}            if(a2==1 && b2==1){System.out.println("1");                   System.out.println("2 1");}            if(a2==1 && b2==0){System.out.println("2");                         System.out.println("2 1");                         System.out.println(n+" "+(n-1));}            if(a2==0 && b2==1){System.out.println("2");                         System.out.println("2 1");                         System.out.println((n-1)+" "+n);}        }        if(a1==1 && b1==1){            if(a2==0 && b2==0){System.out.println("0");}            if(a2==1 && b2==1){System.out.println("2");                         System.out.println("1 2");                         System.out.println("2 1");}            if(a2==1 && b2==0){System.out.println("1");                   System.out.println((n-1)+" "+n);}            if(a2==0 && b2==1){System.out.println("1");                   System.out.println((n)+" "+(n-1));}        }       }    }}
