import java.util.*;public class Main {	public static void main(String[] args)	{		Scanner in=new Scanner(System.in);		int t,n,m;		t=in.nextInt();		while(t--!=0)		{			int a[][]=new int[2][2],u=0;			n=in.nextInt();			m=in.nextInt();			for(int k=0;k<n;k++)			{				for(int i=0;i<2;i++)					for(int j=0;j<2;j++)						a[i][j]=in.nextInt();				if(a[0][1]==a[1][0])					u=1;			}			if(u==1&&m%2==0)				System.out.println("YES");			else				System.out.println("NO");		}		in.close();	}}   	   					  						  				 	  	
