//package com.company; import java.util.StringTokenizer;import java.io.*; public class Main {    public static void main(String[] args) throws Exception {        Reader.init(System.in);        int T = Reader.nextInt();        while ((T--) > 0) {            int n = Reader.nextInt();            int m = Reader.nextInt();            int[] a = new int[4];            boolean ok = false;            for (int j = 1; j <= n; ++j) {                for (int i = 1; i <= 4; ++i) {                    a[i - 1] = Reader.nextInt();                }                if (a[1] == a[2]) ok = true;            }            if (m % 2 == 1) ok = false;            System.out.println(ok ? "YES" : "NO");        }    }       /***     * Reader.init(System.in);     */    static class Reader {        static BufferedReader reader;        static StringTokenizer tokenizer;        static void init(InputStream input) {            reader = new BufferedReader(   //为输入做初始化，以空格分辨每一个输入                    new InputStreamReader(input));            tokenizer = new StringTokenizer("");        }        static String next() throws IOException {            while (!tokenizer.hasMoreTokens()) {   //读取一个或一组字符串                //TODO add check for eof if necessary                tokenizer = new StringTokenizer(                        reader.readLine());            }            return tokenizer.nextToken();        }        static int nextInt() throws IOException {     //将next()读取的字符串转化成整型            return Integer.parseInt(next());        }        static double nextDouble() throws IOException {   //读next()读取的字符串转化成双精度型            return Double.parseDouble(next());        }    }}
