import java.util.*;public class HelpKingdomOfFarFarAway2{    public static void main(String args[]){        Scanner sc= new Scanner(System.in);        String s=sc.next(), ans="";        boolean flag=true;        if(s.charAt(0)=='-'){            s=s.substring(1);            flag=false;        }        int i;        for(i=0;i<s.length();i++){            if(s.charAt(i)=='.')                break;        }        for(int k=1,j=i-1;j>=0;j--,k++){            ans=s.charAt(j)+ans;            if(k%3==0 && j!=0){                ans=','+ans;                k=-3;            }        }        ans+='.';        if(i+1<s.length())            ans+=s.charAt(i+1);        else            ans+='0';        if(i+2<s.length())            ans+=s.charAt(i+2);        else            ans+='0';        ans='$'+ans;        if(!flag)            ans='('+ans+')';        System.out.println(ans);    }}
