//package codechef; import java.util.*;import java.io.*;public class CodeChef{    public static long arr_max(long arr[])    {        long max = 0;        for(int i = 0; i<arr.length; i++)        {            if(arr[i]>max) max = arr[i];        }        return max;    }    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static int fact(int n)    {        int fact = 1;        for(int i = 1; i<=n; i++) fact*=i;        return fact;    }    public static boolean isPrime(int n)    {        if(n==2) return true;        else if(n%2==0) return false;        for(int i = 2; i<=Math.sqrt(n); i++)        {            if(n%i==0)            {                return false;            }        }        return true;    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int t=Integer.parseInt(br.readLine());        while(t-->0){            String st[]=br.readLine().trim().split("\\s+");            int n=Integer.parseInt(st[0]);            int k=Integer.parseInt(st[1]);            String str[]=br.readLine().trim().split("\\s+");             ArrayList<Integer> arr=new ArrayList<>();               for(int i=0;i<n;i++){                  arr.add(Integer.parseInt(str[i]));                }          Collections.sort(arr,Collections.reverseOrder());         if(k==0){            System.out.println(arr.get(0)-arr.get(n-1));            continue;        }        long sum = arr.get(0);        int  i = 1;        while(i<=k && i<n){            sum+= arr.get(i);            i++;        }        System.out.println(sum);                                        }    }}
