	// Main Code at the Bottom	import java.util.*;	import java.io.*; 	public class Main{		//Fast IO class	    static class FastReader {	        BufferedReader br; 	        StringTokenizer st; 	        public FastReader() {	        	boolean env=System.getProperty("ONLINE_JUDGE") != null;	        	//env=true;	        	if(!env) {	        		try {						br=new BufferedReader(new FileReader("src\\input.txt"));					} catch (FileNotFoundException e) {						e.printStackTrace();					}	        	}	        	else br = new BufferedReader(new InputStreamReader(System.in)); 	        } 	        String next() {	            while (st == null || !st.hasMoreElements()) {	                try {	                    st = new StringTokenizer(br.readLine()); 	                } 	                catch (IOException  e) {	                    e.printStackTrace(); 	                } 	            } 	            return st.nextToken(); 	        } 	        int nextInt() {	            return Integer.parseInt(next()); 	        } 	        long nextLong() {	            return Long.parseLong(next()); 	        } 	        double nextDouble() {	            return Double.parseDouble(next()); 	        } 	        String nextLine() {	            String str = ""; 	            try {	                str = br.readLine(); 	            } 	            catch (IOException e) {	                e.printStackTrace(); 	            } 	            return str; 	        } 	    }     	    static long MOD=(long)1e9+7;	    //debug	    static void debug(Object... o) {	        System.out.println(Arrays.deepToString(o));	    }	    static FastReader sc=new FastReader();	    static PrintWriter out=new PrintWriter(System.out);  	    //Global variables and functions	    //Main function(The main code starts from here)	    public static void main (String[] args) throws java.lang.Exception {	    	int test=1;	    	test=sc.nextInt();	    	while(test-->0) {	    		char s[]=sc.nextLine().toCharArray();	    		long n=s.length;	    		long ans=10*(s[0]-'0'-1)+(n*(n+1))/2;	    		out.println(ans);	    			    					    	}	        out.flush();	        out.close();	    }	}
