import java.util.*; public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        int t = input.nextInt();        while (t-- > 0) {            int n = input.nextInt();            int m = input.nextInt();            int[] nums = new int[n];            int sum = 0;            for (int i = 0 ; i < n ; i ++){                nums[i] = input.nextInt();                sum += nums[i];            }            if (sum == m)System.out.println("YES");            else System.out.println("NO");        }    }}
