import java.io.*;import java.util.StringTokenizer;// نورت الكود يا كبير اتفضل // يا رب Acceptedpublic class problem_6 {    public static void main(String[] args) {        FastReader in = new FastReader();        PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));         int t = in.nextInt();        while (t-- > 0) {            int n = in.nextInt();            int[][] arr = new int[n][n];            for (int i = 0; i < n; i++) {                for (int j = 0; j < n; j++) {                    if (n % 2 != 0 && i == 0 && j == n / 2)                        out.print(1 + " ");                    else if (n % 2 != 0 && i == n / 2 && j == 0)                        out.print(1 + " ");                    else                        out.print((i == j || i + j == n - 1) ? 1 + " " : 0 + " ");                }                out.println();            }        }         out.close();    }     private static class FastReader {        BufferedReader br;        StringTokenizer st;         FastReader() {            br = new BufferedReader(new                    InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }         long nextLong() {            return Long.parseLong(next());        }         double nextDouble() {            return Double.parseDouble(next());        }         String nextLine() {            String str = "";            try {                str = br.readLine();            } catch (IOException e) {                e.printStackTrace();            }            return str;        }    }}
