import java.util.Scanner; public class Task6 {    public static void main(String[] args)    {        Scanner scanner = new Scanner(System.in);        int tt;         tt = Integer.parseInt(scanner.nextLine());        while ((tt--) != 0)        {            int n;            n = Integer.parseInt(scanner.nextLine());            for (int i = 0;i < n;i++)            {                for (int j = 0;j < n;j++)                {                    if (i == j || j == (i + 1) % n)                    {                        System.out.print(1);                        System.out.print(" ");                    }                     else                    {                        System.out.print(0);                        System.out.print(" ");                    }                }                System.out.print("\n");            }        }    } }
