import java.util.*;import java.math.*;public class ReverseBinary{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			int runs = sc.nextInt();			while(runs-->0)			{				int size = sc.nextInt();				char[] arr = sc.next().toCharArray();				int ou = 0;				for(int i = 0;i<size-1;i++)				{					if(arr[i]==arr[i+1])					{						ou++;											}				}				System.out.println((ou+1)/2);			}		}}
