import java.util.*;import java.io.*; public class Solution{	static PrintWriter out = new PrintWriter(System.out);	public static void main (String[] args) throws IOException{		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));				String[] input = br.readLine().trim().split(" ");				int numTestCases = Integer.parseInt(input[0]);		while(numTestCases-- > 0){		    input = br.readLine().trim().split(" ");		    int n = Integer.parseInt(input[0]);		    int k = Integer.parseInt(input[1]);		    int[] arr = new int[n * k];		    input = br.readLine().trim().split(" ");		    for(int i = 0; i < n * k; i++){		        arr[i] = Integer.parseInt(input[i]);		    }		    out.println(maxSum(arr, n, k));		}				out.flush();		out.close();	}	public static long maxSum(int[] arr, int n, int k)	{	    long ans = 0;	    int i = n * k -  (n - ((n + 1) / 2) + 1);        int count = 0;	    while(i >= 0 && count < k){	        ans += arr[i];	         i -= (n - ((n + 1) / 2) + 1);	         count++;	    }	    return ans;	}}
