    sys  # sys.stdin = open('input.txt','r+') from collections import defaultdict   t = int(input())for _ in range(t):	T = 1000000	a,b = map(int, input().split())	s = input()	arr = [] 	count=0	i,j=0,len(s)-1	while i < len(s) and s[i] == '0':		i+=1	while j > -1 and s[j] == '0':		j-=1		s=s[i:max(i,j)+1]	 	if s:		val = s[0]	else:		val='1'		T=0 	for i in range(len(s)):		if val != s[i]:			if val == '0':				arr.append(count)			val=s[i]			count=0				count+=1  	if val == '0':		arr.append(count)		if arr:		dp = [[0,0] for _ in range(len(arr))]		dp[0][0]=a+b*arr[0]		dp[0][1]=2*a		for i in range(1,len(arr)):			dp[i][0] = min(dp[i-1][0],dp[i-1][1]) + b*arr[i]			dp[i][1] = min(dp[i-1][0],dp[i-1][1])+a		print(min(dp[len(arr)-1][0],dp[len(arr)-1][1]))	else:		print(min(T,a))	 # sys.stdin.close()
