import java.util.*;import java.io.*;  public class Main2 {	static class FastReader     {         BufferedReader br;         StringTokenizer st;          public FastReader()         {             br = new BufferedReader(new                     InputStreamReader(System.in));         }          String next()         {             while (st == null || !st.hasMoreElements())             {                 try                {                     st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e)                 {                     e.printStackTrace();                 }             }             return st.nextToken();         }          int nextInt()         {             return Integer.parseInt(next());         }          long nextLong()         {             return Long.parseLong(next());         }          double nextDouble()         {             return Double.parseDouble(next());         }          String nextLine()         {             String str = "";             try            {                 str = br.readLine();             }             catch (IOException e)             {                 e.printStackTrace();             }             return str;         }     } 	static FastReader sc = new FastReader();	public static void main (String[] args) {		PrintWriter out = new PrintWriter(System.out);		int t = 1;    	t = sc.nextInt();    	while(t-->0) {    		long p = sc.nextLong();    		long q = sc.nextLong();    		if(p<q || p%q!=0) {    			out.write(p+"\n");    			continue;    		}    		long j = 2L;    		ArrayList<Long> a = new ArrayList<>();    		long ans = q;    		while(j*j<=q) {    			if(ans%j == 0) {    				a.add(j);        			while(ans%j == 0) ans/=j;    			}    			j++;    		}    		if(ans>1) {    			a.add(ans);    			ans = 1;    		}    		    		for(long l : a) {    			long k = p;    			while(k%q == 0) k/=l;    			ans = Math.max(k, ans);    		}    		out.write(ans+"\n");      	}		out.close();	}}
