# Python3 program to print prime factors# and their powers.import math # Function to calculate all the prime# factors and count of every prime factor  def factorize(n):    factors = []    count = 0     # count the number of    # times 2 divides    while ((n % 2 > 0) == False):         # equivalent to n = n / 2;        n >>= 1        count += 1     # if 2 divides it    if (count > 0):        factors.append(2)     # check for all the possible    # numbers that can divide it    for i in range(3, int(math.sqrt(n)) + 1):        count = 0        while (n % i == 0):            count += 1            n = int(n / i)        if (count > 0):            factors.append(i)        i += 2     # if n at the end is a prime number.    if (n > 2):        factors.append(n)     return factors  t = int(input())for _ in range(t):    p, q = map(int, input().split())    ans = -1    fact = factorize(q)    for i in fact:        x = p        while x % q == 0:            x = x//i        ans = max(ans, x)    print(ans)
