import java.io.*;import java.util.StringTokenizer;// نورت الكود يا كبير اتفضل // يا رب Acceptedpublic class NumbersBox {    public static void main(String[] args) {        FastReader in = new FastReader();        PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));         int t = in.nextInt();        while (t-- > 0) {            boolean zero = false;             int n = in.nextInt();            int m = in.nextInt();            int neg = 0;            int sum = 0;            int min = 101;             int[][] arr = new int[n][m];            for (int i = 0; i < n; i++) {                for (int j = 0; j < m; j++) {                    arr[i][j] = in.nextInt();                    if (arr[i][j] == 0)                        zero = true;                    min = Math.min(min, Math.abs(arr[i][j]));                    if (arr[i][j] < 0)                        neg++;                    sum += Math.abs(arr[i][j]);                }            }             out.println((zero || neg % 2 == 0) ? sum : sum - min - min);        }         out.close();    }     private static class FastReader {        BufferedReader br;        StringTokenizer st;         FastReader() {            br = new BufferedReader(new                    InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }    }}
