// practice with rainboyimport java.io.*;import java.util.*; public class CF145B extends PrintWriter {	CF145B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF145B o = new CF145B(); o.main(); o.flush();	} 	char[] cc;	void compose(int b, char c, int i, int di, int j, int dj) {		int n = 0;		for (int h = 0; h < b; h++) {			int d = 1;			if (h == i)				d = di;			else if (h == j)				d = dj;			while (d-- > 0)				cc[n++] = c;			c = c == '4' ? '7' : '4';		}	}	void main() {		int a4 = sc.nextInt();		int a7 = sc.nextInt();		int a47 = sc.nextInt();		int a74 = sc.nextInt();		if (Math.abs(a47 - a74) > 1) {			println(-1);			return;		}		int n = a4 + a7, b = a47 + a74 + 1, b4, b7;		cc = new char[n];		if (a47 > a74) {	// 4747			b4 = b7 = a47;			if (a4 >= b4 && a7 >= b7)				compose(b, '4', 0, a4 - b4 + 1, b - 1, a7 - b7 + 1);			else {				println(-1);				return;			}		} else if (a47 < a74) {	// 7474			b4 = b7 = a74;			if (a4 >= b4 && a7 >= b7)				compose(b, '7', 1, a4 - b4 + 1, b - 2, a7 - b7 + 1);			else {				println(-1);				return;			}		} else {			// 47474			int a = a47;			b4 = a + 1; b7 = a;			if (a4 >= b4 && a7 >= b7)				compose(b, '4', 0, a4 - b4 + 1, b - 2, a7 - b7 + 1);			else {				// 74747				b4 = a; b7 = a + 1;				if (a4 >= b4 && a7 >= b7)					compose(b, '7', 1, a4 - b4 + 1, b - 1, a7 - b7 + 1);				else {					println(-1);					return;				}			}		}		println(cc);	}}
