import java.util.*;public class MyClass {     public static void main(String args[]) {      Scanner sc=new Scanner(System.in);      int t=sc.nextInt();      while(t-->0)      {           int x=sc.nextInt();          int i=1;          int s=0,count=0;          while(s<x)          {              s=s+i;              i++;              count++;          }          int d=s-x;          if(s==x)          System.out.println(count);          else{          if(d==1)          System.out.println(count+1);          else          System.out.println(count);          }      }    }}
