import java.io.*;public class Main{	public static void main(String args[])throws IOException{		BufferedReader in=new BufferedReader(new InputStreamReader(System.in));		int n;		String cadena;					n=Integer.parseInt(in.readLine());		cadena=in.readLine();				int sum1=0;		int sum2=0;		int i;				if(n%2==0&&n==cadena.length()){			for(i=0;i<cadena.length()/2;i++){				if(cadena.charAt(i)!='4'&&cadena.charAt(i)!='7'){					System.out.println("NO");					return;							}				else {					sum1+=Integer.parseInt(""+cadena.charAt(i));				}				if(cadena.charAt(n-i-1)!='4'&&cadena.charAt(n-i-1)!='7'){					System.out.println("NO");					return;							}				else{					sum2+=Integer.parseInt(""+cadena.charAt(n-i-1));				}			}		}		else{			System.out.println("NO");			return;		}				if(sum1==sum2)			System.out.println("YES");		else			System.out.println("NO");	}}
