/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *///package th1; /** * * @author Dell */import java.util.*; public class Bai04 {     public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        String tmp = sc.nextLine();        String s = sc.nextLine();         if(isLucky(s) && isSum(s))            System.out.println("YES");        else System.out.println("NO");    }        public static boolean isLucky(String x){        for(int i = 0; i < x.length(); ++i){            if(x.charAt(i)!='4' && x.charAt(i)!='7') return false;        }        return true;    }        public static boolean isSum(String x){        int l = 0, r = x.length() - 1;        int sL = 0, sR = 0;        while(l<r){            sL += (x.charAt(l) - '0');            sR += (x.charAt(r) - '0');            l++; r--;        }        return sL == sR;    }}
