import java.util.*;public class Main {    public static void main(String[] args) {        Scanner scn = new Scanner(System.in);        int t = scn.nextInt();        while (t-- > 0) {            int n = scn.nextInt();            int[] arr = new int[n];            for (int i = 0; i < n; i++)                arr[i] = scn.nextInt();            int si=0;            int ei=n-1;            while(si<ei){                System.out.print(arr[si]+" ");                System.out.print(arr[ei]+" ");                si++;                ei--;            }            if(si==ei){                System.out.print(arr[si]+" ");            }            System.out.println();        }    }}
