import java.util.*; public class Main{       public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		int t = scan.nextInt();		while(t>0){		    int a = scan.nextInt();		    int b = scan.nextInt();		    int c = scan.nextInt();		    t--;		    		    if((a+b+c)%9 ==0 && Math.min(a, Math.min(b,c))>= (a+b+c)/9){		        System.out.println("YES");		    }		    else{		        System.out.println("NO");	        }                		}	}}
