import java.io.*;import java.lang.*;import java.util.*;import java.io.*;import java.util.*;public class Main{	static Scanner scanner=new Scanner(System.in);	public static void main(String[] args) {		int t=scanner.nextInt();		while(t-->0) {			StringBuilder sb=new StringBuilder();			int n=scanner.nextInt();			int a[]=new int [n];			for(int i=0;i<n;i++)a[i]=scanner.nextInt();			int b[]=new int [n],c[]=new int[n];			long compare=0;			for(int i=0;i<n;i++) {				if(i%2==0) {					compare+=a[i]-1;					b[i]=1;				}else {					b[i]=a[i];				}			}			for(int i=0;i<n;i++) {				if(i%2==1) {					compare-=a[i]-1;					c[i]=1;				}else {					c[i]=a[i];				}			}			if(compare<0) {				for(int i=0;i<n;i++)sb.append(b[i]+" ");			}else {				for(int i=0;i<n;i++)sb.append(c[i]+" ");			}			System.out.println(sb);		}	}} 		 	    		 					 	 	   	  	  	
