import java.util.*;public class Solution{      public static void main(String []args){        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        while(t-->0)     {      HashSet<Long> h=new HashSet<>();   int n=sc.nextInt();  long a[]=new long[n];  for(int i=0;i<n;i++)  {      a[i]=sc.nextLong();        }  for(int i=0;i<n;i++)  {      for(int j=i+1;j<n;j++)      {          h.add(Math.abs(a[j]-a[i]));      }  }           System.out.println(h.size());              }                           }     }
