import java.util.*;public class Main{public static void main(String args[]){    Scanner sc=new Scanner(System.in);    int t=sc.nextInt();    for(int i=0;i<t;i++)    {      int a=sc.nextInt();      int b=sc.nextInt();      int n=sc.nextInt();      int ans=1;      while(a%2!=1 || b%2!=1)      {       if(a%2==0)       {           a=a/2;           ans=ans*2;       }else if(b%2==0){           b=b/2;           ans=ans*2;                  }      }      if(ans>=n)      {      System.out.println("YES");      }else{          System.out.println("NO");      }        }        }}
