import java.util.*;public class Main {    public static void main(String[]args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t-- > 0) {            int n = sc.nextInt();            int c1 = 0, c2 = 0;            for(int i=0; i<n; i++) {                int m = sc.nextInt();                if(m == 1) c1++;                else c2++;            }            if(c1%2 == 0  &&  c1 != 0) {                System.out.println("YES");                continue;            }            if(c1 == 0 && c2%2 == 0) {                System.out.println("YES");                continue;            }            System.out.println("NO");        }    }}
