import java.util.*;import java.io.*; public class Main{	public static void main(String[] args) {				Scanner sc= new Scanner(System.in);				int n=sc.nextInt();				while(n-->0) {			int m=sc.nextInt();			int ones=0;			int twos=0;						while(m-->0) {				if(sc.nextInt()==1) {					ones++;				}else {					twos++;				}			}						if(twos%2==1) {				if(ones%2==0) {					if(ones>=2) {						System.out.println("YES");										}else {						System.out.println("NO");					}				}else {					System.out.println("NO");				}			}else {				if(ones%2==0) {					System.out.println("YES");				}else {					System.out.println("NO");				}			}		}			}		}
