import java.util.*;public class template{    public static void main(String args[])    {        Scanner in=new Scanner(System.in);        int t=in.nextInt();        int i,j;        while(t-->0)        {            int n=in.nextInt();            String b=in.next();            int x;            int flag=Integer.parseInt(b.charAt(0)+"")==0?1:2;            System.out.print(1);            for(i=1;i<n;i++)            {                x=Integer.parseInt(b.charAt(i)+"");                if((x+1)==flag)                {                    System.out.print(0);                    flag=x+0;                }                else                {                    System.out.print(1);                    flag=x+1;                }            }            System.out.println();        }    }}//https://codeforces.com/problemset/problem/1466/C    
