import java.util.Arrays;import java.util.Scanner;public class Main {   static boolean isPrime(long n ) {	   if(n%2==0) return false;	   long root = (long) Math.sqrt(n);	   for(int i=3;i<=root;i+=2) {		   if(n%i==0) return false;	   }	   return true;   }	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int tc = sc.nextInt();		boolean is_prime[] = new boolean[100001];		Arrays.fill(is_prime, true);		is_prime[0] = is_prime[1] = false;		for (int i = 2; i * i <= 100000; i++) {		    if (is_prime[i]) {		        for (int j = i * i; j <= 100000; j += i)		            is_prime[j] = false;		    }		}		while(tc-->0) {				 int d = sc.nextInt();				 int count=2,ans=1,i=2,last=1;				 if(d==1) {					 System.out.println(6);					 continue;				 }				  while(count>0) {					  if(is_prime[i] && i-last>=d) {						  ans*=i;						  count--;						  last=i;						  i+=d-1;					  }					  i++;					  				  }				 System.out.println(ans);				 		}		sc.close();	}}
