import java.util.*;import java.lang.*;import java.io.*;public class NewYearsNumber{    	public static void main(String args[])throws IOException{		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));		BufferedWriter bw = new BufferedWriter(new OutputStreamWriter(System.out));		int t = Integer.parseInt(br.readLine());        while(t-->0){            int n = Integer.parseInt(br.readLine());            int rem = n%2020;            int q = n/2020;            if(rem<=q){                bw.write("Yes\n");            }            else{                bw.write("No\n");            }        }		bw.close();	}}
