//package codechef;Â import java.io.*;public class CodeChef{    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int t = Integer.parseInt(br.readLine());        while(t-->0)        {            String s = br.readLine();            for(int i = 0; i<s.length(); i++)            {                if(s.charAt(i++)=='a')                {                    System.out.print('b');                }                else System.out.print('a');                if(i<s.length())                {                    if(s.charAt(i)=='z')                    {                        System.out.print('y');                    }                    else System.out.print('z');                }            }            System.out.println();        }    }}
