# coding: utf-8 from math import ceilfrom sys import stdin, stdout for ct in range(int(stdin.readline().strip())):  A, B, n = map(int, stdin.readline().strip().split())  a = list(map(int, stdin.readline().strip().split()))  b = list(map(int, stdin.readline().strip().split()))    for i in range(n):    B -= a[i] * ceil(b[i] / A)      stdout.write('YES\n') if B + max(a) > 0 else stdout.write('NO\n')
