// package FinalGrind; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.ArrayList;import java.util.StringTokenizer; public class Problem4 {    public static void main(String[] args)throws IOException {        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));        int n=Integer.parseInt(br.readLine());        int a[]=new int[n+1];        StringTokenizer st=new StringTokenizer(br.readLine());        for(int i=1;i<=n;i++){            a[i]=Integer.parseInt(st.nextToken());        }        int inc[]=new int[n+1];        int dec[]=new int[n+1];        inc[1]=dec[1]=1;        int maxl=2;        for(int i=2;i<=n;i++){            if(a[i]>a[i-1]){                inc[i]=inc[i-1]+1;                dec[i]=1;            }            else{                dec[i]=dec[i-1]+1;                inc[i]=1;            }            maxl=Math.max(maxl,Math.max(inc[i],dec[i]));        }        int count=0;        ArrayList<Integer> ind1=new ArrayList<>();        ArrayList<Integer> ind2=new ArrayList<>();        for(int i=2;i<=n;i++){            if(inc[i]==maxl){                count++;                ind1.add(i);            }            if(dec[i]==maxl){                count++;                ind2.add(i);            }        }        if(count==2&&maxl%2!=0&&ind1.size()==ind2.size()&&ind1.get(0)<ind2.get(0)){            System.out.println(1);        }        else{            System.out.println(0);        }    }}
