// package Practice; import java.util.Scanner; public class CF015B {    public static void main(String[] args) {        Scanner s = new Scanner(System.in);        int t = s.nextInt();        while(t-- > 0){            long nRow = s.nextLong();            long nCol = s.nextLong();            long x1 = s.nextLong();            long y1 = s.nextLong();            long x2 = s.nextLong();            long y2 = s.nextLong();            long dx = Math.abs(x1 - x2);            long dy = Math.abs(y1 - y2);            long ans = (nRow - dx) * (nCol - dy) * 2;            if(nRow - dx >= dx + 1 && nCol - dy >= dy + 1) {                ans -= (nRow - 2 * dx) * (nCol - 2 * dy);            }            ans = nRow * nCol - ans;            System.out.println(ans);        }    }}
