//I AM THE CREED/* //I AM THE CREED/* package codechef; // don't place package name! */import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.StringTokenizer; import java.util.*;import java.awt.Point;public class Main{    static final Random random=new Random();           public static void main(String[] args) throws IOException     {         FastScanner input=new FastScanner();        int tt=1;        while(tt!=0){            int n=input.nextInt();            int m=input.nextInt();            int k=input.nextInt();            long MOD=1000000007;            ArrayList<ArrayList<Integer>> graph=new ArrayList<>();            for(int i=0;i<n;i++){                graph.add(new ArrayList<Integer>());            }            for(int i=0;i<n;i++){                for(int j=Math.min(i+k-1, n-1);j>i;j--){                    if((k-(j-i+1))%2==0 && i-((k-(j-i+1))/2)>=0 && j+((k-(j-i+1))/2)<n){                        graph.get(i).add(j);                        graph.get(j).add(i);                    }                }            }            long components=0;            HashSet<Integer> visited=new HashSet<>();            for(int i=0;i<n;i++){                if(visited.contains(i)){                    continue;                }                dfs(graph, i, visited);                components++;            }            System.out.println(pow((long)(m), components, MOD));            tt--;        }            }    static void dfs(ArrayList<ArrayList<Integer>> graph, int node, HashSet<Integer> visited){        if(visited.contains(node)){            return;        }        visited.add(node);        for(int child:graph.get(node)){            dfs(graph, child, visited);        }    }    static long pow(long num, long exp, long mod){        long ans=1;        for(int i=1;i<=exp;i++){            ans=(ans*num)%mod;        }        return ans;    }    static boolean isPrime(int n)    {         // Check if number is less than        // equal to 1        if (n <= 1)            return false;         // Check if number is 2        else if (n == 2)            return true;         // Check if n is a multiple of 2        else if (n % 2 == 0)            return false;         // If not, then just check the odds        for (int i = 3; i <= Math.sqrt(n); i += 2)         {            if (n % i == 0)                return false;        }        return true;    }    static int gcd(int a, int b)    {        if (a == 0)            return b;         return gcd(b % a, a);     }         // method to return LCM of two numbers    static int lcm(int a, int b)    {        return (a / gcd(a, b)) * b;    }         //Credits to SecondThread(https://codeforces.com/profile/SecondThread) for this tempelate    static void ruffleSort(int [] a) {		int n=a.length;//shuffle, then sort 		for (int i=0; i<n; i++) {			int oi=random.nextInt(n);			int temp=a[oi];			a[oi]=a[i]; a[i]=temp;		}		Arrays.sort(a);	}    //Credits to SecondThread(https://codeforces.com/profile/SecondThread) for this tempelate.    static class FastScanner {		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st=new StringTokenizer("");		String next() {			while (!st.hasMoreTokens())				try {					st=new StringTokenizer(br.readLine());				} catch (IOException e) {					e.printStackTrace();				}			return st.nextToken();		}				int nextInt() {			return Integer.parseInt(next());		}		int[] readArray(int n) {			int[] a=new int[n];			for (int i=0; i<n; i++) a[i]=nextInt();			return a;		}		long nextLong() {			return Long.parseLong(next());		}	}        }
