#include <bits/stdc++.h>#define MOD 1000000007#define all(v) v.begin(),v.end()#define ninpo ios_base::sync_with_stdio(false);cin.tie(NULL);#define dbg(x) cout<<#x<<" = "<<(x)<<endl;#define sz(cntainer) (int)cntainer.size()#define tc int t;cin>>t;for(int i=0;i<t;++i)#define ll long long#define pii pair<int,int>using namespace std; ll binpow(ll a, ll b){    if(!b)return 1;    if(b==1)return a;    ll t1=binpow(a,b/2);    t1=(t1*t1)%MOD;    if(b&1)t1=(t1*a)%MOD;    return t1;}void solve(){    ll n,m,k;    cin>>n>>m>>k;    if(k>n){        cout<<binpow(m,n)<<endl;        return;    }    if(k==n){        ll ans=binpow(m,(n+1)/2);        cout<<ans;        return;    }    if(k==1){        cout<<binpow(m,n);    }    else if(k&1){        ll m1=m;        cout<<(((m*(m-1)))%MOD + m)%MOD;    }else{        cout<<m;    }   } int main(){       #ifndef ONLINE_JUDGE           freopen("input.txt", "r", stdin);           freopen("output.txt", "w", stdout);       #endif   ninpo;    solve();}
