import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int a=sc.nextInt();        int b=sc.nextInt();        int c=sc.nextInt();        int d=sc.nextInt();        int e=sc.nextInt();        int f=sc.nextInt();        int g=sc.nextInt();        int h=sc.nextInt();        int a1=(b*c)/g;        int a2=d*e;        int a3=f/h;        int ans1=Math.min(a1,a2);        int ans=Math.min(ans1,a3);        System.out.println(ans/a);                    }        }
