//package questions;import java.util.* ;public class marks {	public static void main(String[] args)	{		Scanner in = new Scanner(System.in) ;		//System.out.println("Enter number of students :");		int n = in.nextInt() ;		//System.out.println("Enter number of subjects :") ;		int sub = in.nextInt() ;		in.nextLine() ;		String a[] = new String[n] ;		//System.out.println("Enter");		for(int i=0 ; i<n ; i++)		{			a[i] = in.nextLine() ;		}		int num=0 ;		for(int i=0 ; i<n ; i++)		{			for(int j=0 ; j<sub ; j++)			{				int curr =  Integer.parseInt(a[i].charAt(j)+"") ;				int max=curr ;				for(int k=0 ; k<n ; k++)				{					max=Math.max(max , Integer.parseInt(a[k].charAt(j)+"")) ;				}				if(max==curr)				{					num++ ;					break ;				}			}		}		System.out.println(num);	}}
