// Coded By : Aj Style// Problem: A. Hometask// Contest: Codeforces - Codeforces Round #109 (Div. 1)// URL: https://codeforces.com/contest/154/problem/A// Memory Limit: 256 MB// Time Limit: 2000 ms #include <bits/stdc++.h>#define ll long long int#define ii int#define du double#define jmp "\n"#define vl vector<ll>#define vvl vector<vl>#define pb push_back#define pq_max priority_queue<ll>#define pq_min priority_queue<ll, vi, greater<ll>>#define printv(v)         \    for (auto x : v)      \        cout << x << " "; \    cout << jmp;#define vi vector<int>#define vb vector<bool>#define ump unordered_map#define SORT(v) sort(v.begin(), v.end())#define REV(x) reverse(x.begin(), x.end())#define all(x) x.begin(), x.end()#define SET(x, a) memset(x, a, sizeof(x))#define si(x) (ll) x.size()#define ff first#define ss second#define I(a)          \    for (auto &x : a) \        cin >> x;#define iin insert#define log(args...)                             \    {                                            \        string _s = #args;                       \        replace(_s.begin(), _s.end(), ',', ' '); \        stringstream _ss(_s);                    \        istream_iterator<string> _it(_ss);       \        err(_it, args);                          \    }#define logarr(arr, a, b)            \    for (int z = (a); z <= (b); z++) \        cout << (arr[z]) << " ";     \    cout << endl;                    \    using namespace std;using namespace std; // vs tokenizer(string str,char ch) {std::istringstream var((str)); vs v; string t; while(getline((var), t, (ch))) {v.pb(t);} return v;} void err(istream_iterator<string> it) {}template <typename T, typename... Args>void err(istream_iterator<string> it, T a, Args... args){    cout << *it << " = " << a << endl;    err(++it, args...);}const ll nax = 1e5 + 5;void solve(){    string s;    cin >> s;    ll m, ans = 0;    cin >> m;    s += "#";    for (ll i = 0; i < m; i++)    {        char a, b;        ll c1 = 0, c2 = 0;        cin >> a >> b;         for (ll j = 0; j < si(s); j++)        {            if (s[j] == a)                c1++;            else if (s[j] == b)                c2++;            else                ans += min(c1, c2), c1 = c2 = 0;                    }    }    cout << ans;}int32_t main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    cout.tie(NULL);    ll t = 1;    // cin >> t;    while (t--)        solve();    return 0;}
