// 155A -  I_love_%username%// https://codeforces.com/problemset/problem/155/A import java.util.Scanner; public class CF155A {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[n];        for (int i = 0; i < n; i++) {            arr[i] = sc.nextInt();        }        int max = arr[0];        int min = arr[0];        int amazing = 0;        for (int i = 1; i < n; i++) {            if (arr[i] > max) {                amazing += 1;                max = arr[i];            }            if (arr[i] < min) {                amazing += 1;                min = arr[i];            }        }        System.out.println(amazing);        sc.close();    }}
