//package com.company; import java.util.*;import java.lang.*;import java.io.*; public class Main {     public static void main(String[] args)throws Exception {        Scanner sx = new Scanner(System.in);        int n = sx.nextInt();        int[][] arr = new int[n][n];        for(int i=0;i<n;i++){            for(int j=0;j<n;j++){                arr[i][j] = sx.nextInt();            }        }        int win=0;        for(int i=0;i<n;i++){            for(int j=0;j<n;j++){                int col =0,row =0;                for(int k=0;k<n;k++){                    row+=arr[i][k];                    col+=arr[k][j];                }                if(row<col){                    win++;                }                //System.out.println(win+" "+row+" "+col);            }        }        System.out.println(win);    }}
