import java.util.Scanner;  public class cForces158A {        public static void main(String[] args){        Scanner scan = new Scanner(System.in);                int n = scan.nextInt();        int[] con = new int[n+1];        int count = 0;        int k = scan.nextInt();                for(int i=1;i<=n;i++){            con[i] = scan.nextInt();        }                for(int i=1;i<=n;i++){                        if(con[i]>=con[k] && con[i]>0) count++;                    }                        System.out.println(count);            }    }
