import java.io.*;import java.util.*; public class Solution{    public static void main(String[] args)throws IOException{        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));        int n=Integer.parseInt(br.readLine());        String[]ip=br.readLine().split(" ");        int c1=0,c2=0,c3=0,c=0;        for(int i=0;i<n;i++){            if(Integer.parseInt(ip[i])==4)             c=c+1;            else if(Integer.parseInt(ip[i])==1)             c1=c1+1;            else if(Integer.parseInt(ip[i])==2)             c2=c2+1;            else if(Integer.parseInt(ip[i])==3)             c3=c3+1;        }        c=c+c3;        c1=(c3>=c1)?0:c1-c3;        if(c2%2==0){         c=c+(c2/2);         c=c+(c1%4==0?c1/4:(c1/4)+1);        }        else{            c2=(c2*2)+c1;            c=c+(c2%4==0?c2/4:(c2/4)+1);        }        System.out.println(c);    }}
