# written with help of passed solutionsfrom math import sqrt n, a, d = map(int, input().split())a = float(a)ans = [0] * n tm = 0for i in range(n):    t, v = map(int, input().split())    acc_t = v / a    add_t = 0     if acc_t ** 2 * a > 2 * d:        add_t = sqrt(2 * d / a)    else:        add_t = acc_t + (d - acc_t ** 2 * a / 2) / v     tm = max(tm, t + add_t)    ans[i] = tm print('\n'.join(map(str, ans)))
