import java.util.Scanner;import java.util.Arrays;public class Ishu{	public static void main(String []args)	{	Scanner scan=new Scanner(System.in);	int n,m,i,j;	String a,s;	char[] ch=new char[100000];	a=scan.next();	s=scan.next();	n=a.length();	m=s.length();	ch=s.toCharArray();	Arrays.sort(ch,0,m);	s=String.valueOf(ch);	char[] A = a.toCharArray();	j=m-1;	for(i=0;i<n&&j>=0;++i)		if(A[i]<s.charAt(j))			{			A[i] = s.charAt(j);			j--;			}    a = String.valueOf(A);	System.out.println(a);	}}
