import java.util.Scanner;import java.lang.Math;public class Ishu{	public static void main(String[] args)	{	Scanner scan=new Scanner(System.in);	long n,sa=0l,sb=0l,quo=1,rem=1;	int hcf=1,i,j,k,lcm=1;	String str1,str2;	char[] A=new char[1000];	char[] B=new char[1000];	n=scan.nextLong();	str1=scan.next();	A=str1.toCharArray();	str2=scan.next();	B=str2.toCharArray();	hcf=(str1.length()<=str2.length())?str1.length():str2.length();	if(str1.length()%hcf==0&&str2.length()%hcf==0);	else		{		for(hcf=(int)Math.sqrt((str1.length()<=str2.length())?(str1.length()):(str2.length()));hcf>1;--hcf)			if(str1.length()%hcf==0&&str2.length()%hcf==0)				break;		}	lcm=(str1.length()*str2.length())/hcf;	quo=n/lcm;	rem=n%lcm;	j=k=0;	for(i=1;i<=lcm;++i)		{		if(j==str1.length())			j=0;		if(k==str2.length())			k=0;		if(!(A[j]==B[k]))			{			if(A[j]=='R'&&B[k]=='S'||A[j]=='S'&&B[k]=='P'||A[j]=='P'&&B[k]=='R')				++sb;			else				++sa;			}		++j;		++k;		}	sa*=quo;	sb*=quo;	j=k=0;	for(i=1;i<=rem;++i)		{		if(j==str1.length())			j=0;		if(k==str2.length())			k=0;		if(!(A[j]==B[k]))			{			if(A[j]=='R'&&B[k]=='S'||A[j]=='S'&&B[k]=='P'||A[j]=='P'&&B[k]=='R')				++sb;			else				++sa;			}		++j;		++k;		}	System.out.println(sa+" "+sb);	}}
