import java.util.Scanner;import java.util.Arrays;public class Ishu{	public static void main(String []args)	{	Scanner scan=new Scanner(System.in);	int n,b,i,sum=0,index=0;	String str;	double temp;		boolean flag=true;	int[] a=new int[100];	n=scan.nextInt();	b=scan.nextInt();	for(i=0;i<n;++i)		{		a[i]=scan.nextInt();		sum+=a[i];		}	sum+=b;		temp=(double)sum/n;	for(i=0;i<n;++i)		if((double)a[i]>temp)			{			System.out.println(-1);			return;			}	for(i=0;i<n;++i)		{		str=String.valueOf(temp-a[i]);		index=str.indexOf('.');		if(str.length()-index<7)			{			while(str.length()-index<7)				str+="0";			}		else if(str.length()-index>7)			str=String.valueOf(Math.round((temp-a[i])*1000000.0)/1000000.0);		System.out.println(str);		}	}}
