# Given 3 coordinates: we can find 4th by using midpoint logic. n, m = [int(ele) for ele in input().strip().split()]districts = []coordinates = []for i in range(n):    row = list(input().strip())    districts.append(row) for i in range(n):    for j in range(m):        if districts[i][j] == '*':            coordinates.append((i+1, j+1)) #coordinates = sorted(coordinates)if coordinates[0][0] != coordinates[1][0] and coordinates[0][1] != coordinates[1][1]:    midpt = (coordinates[0][0]+coordinates[1][0], coordinates[0][1]+coordinates[1][1])    print(str(midpt[0] - coordinates[2][0]) + ' ' + str(midpt[1] - coordinates[2][1])) elif coordinates[0][0] != coordinates[2][0] and coordinates[0][1] != coordinates[2][1]:    midpt = (coordinates[0][0]+coordinates[2][0], coordinates[0][1]+coordinates[2][1])    print(str(midpt[0] - coordinates[1][0]) + ' ' + str(midpt[1] - coordinates[1][1])) else:    midpt = (coordinates[1][0]+coordinates[2][0], coordinates[1][1]+coordinates[2][1])    print(str(midpt[0] - coordinates[0][0]) + ' ' + str(midpt[1] - coordinates[0][1]))
