import java.util.*;public class Berland {	public static void main(String args[])	{		Scanner in=new Scanner(System.in);		int i,j,p=0,q=0;		int n=in.nextInt();		int m=in.nextInt();		int a[][]=new int[3][2];		for(i=0;i<3;i++)		{			for(j=0;j<2;j++)			{				a[i][j]=0;			}		}		String s[][]=new String[n][1];		for(i=0;i<n;i++)		{			s[i][0]=in.next();		}		for(i=0;i<n;i++)		{			for(j=0;j<m;j++)			{				if(s[i][0].charAt(j)=='*')				{					a[p][q]=i;					q=q+1;					a[p][q]=j;					p=p+1;					q=0;				}			}		}		if(a[0][0]==a[1][0])		{			System.out.print((a[2][0]+1)+" ");		}		else if(a[0][0]==a[2][0])		{			System.out.print((a[1][0]+1)+" ");		}		else		{			System.out.print((a[0][0]+1)+" ");		}		if(a[0][1]==a[1][1])		{			System.out.println(a[2][1]+1);		}		else if(a[0][1]==a[2][1])		{			System.out.println(a[1][1]+1);		}		else		{			System.out.println(a[0][1]+1);		}	}}
