import java.util.*;// scanner.nextInt();public class Solution {    public static void main(String[] args) {                Scanner scanner = new Scanner(System.in);        int n = scanner.nextInt();        boolean[][] dots = new boolean[2001][2001];        int[][] array = new int[n][2];                            for(int i = 0; i < n; ++i)        {            array[i][0] = scanner.nextInt()+1000;            array[i][1] = scanner.nextInt() + 1000;            dots[array[i][0]][array[i][1]] = true;        }                int res = 0;                for(int i = 0; i < n; ++i)        {            for(int j = i+1; j < n; ++j)            {                if((array[i][0] + array[j][0]) % 2 == 1 || (array[i][1] + array[j][1]) % 2 == 1) continue;                                int x = (array[i][0] + array[j][0])/2;                int y = (array[i][1] + array[j][1])/2;                                if(dots[x][y]) ++res;                                  }        }        System.out.println(res);    }}Â 
