import java.util.*;import java.math.*;public class ComparingStrings{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			char[] first = sc.nextLine().toCharArray();			char[] second = sc.nextLine().toCharArray();			int[] letters = new int[26];			Arrays.fill(letters,0);			if(first.length!=second.length)			{				System.out.println("NO");				return;			}			if(first.length==1)			{				if(first[0]!=second[0])					System.out.println("NO");				else					System.out.println("YES");				return;			}			int count = 0;			for(int i = 0;i<first.length;i++)			{				if(first[i]!=second[i])					count++;				letters[(int)first[i]-97]++;				letters[(int)second[i]-97]--;			}			for(int i = 0;i<26;i++)			{				if(letters[i]!=0)				{					System.out.println("NO");					return;				}			}			if(count==2)				System.out.println("YES");			else				System.out.println("NO");		}}
